#!/usr/bin/env python

# These two lines are only needed if you don't put the script directly into
# the installation directory
import sys
sys.path.append('/usr/share/inkscape/extensions')
import base64
# We will use the inkex module with the predefined Effect base class.
import inkex
# The simplestyle module provides functions for style parsing.
from simplestyle import *

class HelloWorldEffect(inkex.Effect):
    """
    Example Inkscape effect extension.
    Creates a new layer with a "Hello World!" text centered in the middle of the document.
    """
    def __init__(self):
        """
        Constructor.
        Defines the "--logo" option of a script.
        """
        # Call the base class constructor.
        inkex.Effect.__init__(self)

        # Define string option "--logo" with "-l" shortcut and default value "anateskilat".
        self.OptionParser.add_option('-l', '--logo', action = 'store',
          type = 'string', dest = 'logo', default = 'anateskilat',
          help = 'Which logo needs your Design?')

    def effect(self):
        """
        Effect behaviour.
        Overrides base class' method and inserts "Hello World" text into SVG document.
        """
        # Get script's "--logo" option value.
        logo = self.options.logo

        # Get access to main SVG document element and get its dimensions.
        svg = self.document.getroot()
        svg.set('width', '794,00000')
        svg.set('height', '1123,00000')
        svg.set('unit', 'mm')
        # or alternatively
        # svg = self.document.xpath('//svg:svg',namespaces=inkex.NSS)[0]

        # Again, there are two ways to get the attibutes:
        pageWidth  = self.unittouu(svg.get('width'))
        pageHeight = self.unittouu(svg.attrib['height'])

        # Create a new layer.
        layer = inkex.etree.SubElement(svg, 'g')
        layer.set(inkex.addNS('label', 'inkscape'), ' %s Logo Layer' % (logo))
        layer.set(inkex.addNS('groupmode', 'inkscape'), 'layer')

        # Create text element
        # text = inkex.etree.Element(inkex.addNS('text','svg'))
        # text.text = 'Hello %s!' % (what)

        # Set text position to center of document.
        # text.set('x', str(width / 2))
        # text.set('y', str(height / 2))

        # Center text horizontally with CSS style.
        # style = {'text-align' : 'center', 'text-anchor': 'middle'}
        # text.set('style', formatStyle(style))

        createdLogo = self._create_image_node(logo, pageWidth, pageHeight)

        # Connect elements together.
        layer.append(createdLogo)


    def _create_image_node(self, logo, pageWidth, pageHeight):
        # Logo Selection
        # base64 code encoder : https://www.base64-image.de
        image = ""
        if logo == "anateskilat":
            image = "data:image/png;base64,"
        if logo == "kadinlarteskilati":
            image = "data:image/png;base64,"
        if logo == "genclikteskilati":
            image = "data:image/png;base64,"
        if logo == "kadinlargenclikteskilati":
            image = "data:image/png;base64,"

        logoWidth = 200
        logoHeight = 60

        attribs = {
            'height': str(logoHeight),
            'width': str(logoWidth),
            'x': str(pageWidth-logoWidth),
            'y': str(pageHeight-logoHeight),
            'preserveAspectRatio': 'None',
            inkex.addNS('href', 'xlink'): image
        }

        createdImage = inkex.etree.Element(inkex.addNS('image', 'svg'), attribs)
        return createdImage

# Create effect instance and apply it.
effect = HelloWorldEffect()
effect.affect()